(9, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Welcome to WooCommerce! Set up your store and start selling', 'We\'re here to help you going through the most important steps to get your store up and running.', '{}', 'actioned', 'woocommerce-admin', '2020-12-05 22:01:47', NULL, 0, 'plain', '', 0, 'info'),
(10, 'mailchimp-for-woocommerce-incomplete-install', 'warning', 'en_US', 'Mailchimp For WooCommerce', 'Plugin is not yet connected to a Mailchimp account. To complete the connection, open the settings page.', '{\"getting_started\":true,\"activated\":1607205893,\"activated_formatted\":\"December 5th\"}', 'unactioned', 'mailchimp-for-woocommerce', '2020-12-05 22:04:53', NULL, 0, 'plain', '', 0, 'info'),
